CREATE DEFINER=`root`@`localhost` FUNCTION `more_books`(role1_p varchar(64), role2_p varchar(64)) RETURNS int
    READS SQL DATA
    DETERMINISTIC
BEGIN
declare count1 int; 
declare count2 int;

if not exists (select name from role_trimmed 
	where name = role1_p)
    or not exists(select name from role_trimmed 
	where name = role2_p)
then signal sqlstate '45000'
	set message_text = 'unknown role name';
end if;

select count(distinct book_id) into count1 
	from role_trimmed
	inner join role_in_book
    on id = role_id
    where name = role1_p;
    
select count(distinct book_id) into count2 
	from role_trimmed
	inner join role_in_book
    on id = role_id
    where name = role2_p;

if (count1 > count2)
	then return 1;
elseif(count1 = count2)
	then return 0;
else
	 return -1;
end if;

end ;;
DELIMITER ;
